(3091, 601, 'head_title', 'Team Scaling'),
(3092, 601, '_head_title', 'field_5d65048e346b9'),
(3093, 601, 'head_content', 'If you lack internal software engineering resources, team to handle your digital transformation initiatives or just would like to implement the specific technical task, our team is ready to maintain the development projects and ensure reliable, proactive and top-standard development process.'),
(3094, 601, '_head_content', 'field_5d65049f346ba'),
(3095, 601, 'head_img', ''),
(3096, 601, '_head_img', 'field_5d6504ad346bb'),
(3097, 601, 'step_title', 'How It Works?'),
(3098, 601, '_step_title', 'field_5d6506bbaf24e'),
(3099, 601, 'step_content', 'We offer managed IT staffing services to our clients, helping to extend their software development teams with talented engineers quickly and efficiently. '),
(3100, 601, '_step_content', 'field_5d6506c9af24f'),
(3101, 601, '1_step', 'Our aim is to evaluate all business and tech challenges to effectively start remote team adaptation.'),
(3102, 601, '_1_step', 'field_5d6506d9af250'),
(3103, 601, '2_step', 'Set up the proper technical team and tune up the business processes and communication.'),
(3104, 601, '_2_step', 'field_5d6506edaf251'),
(3105, 601, '3_step', 'Ongoing monitoring of the development process in order to keep the high-efficiency level.'),
(3106, 601, '_3_step', 'field_5d6506f9af252'),
(3107, 601, '1_step_title', 'Kick Start '),
(3108, 601, '_1_step_title', 'field_5d6507413febc'),
(3109, 601, '2_step_title', 'Integration'),
(3110, 601, '_2_step_title', 'field_5d65074e3febd'),
(3111, 601, '3_step_title', 'Operation '),
(3112, 601, '_3_step_title', 'field_5d6507603febe'),
(3115, 605, '_menu_item_type', 'post_type'),
(3116, 605, '_menu_item_menu_item_parent', '0'),
(3117, 605, '_menu_item_object_id', '109'),
(3118, 605, '_menu_item_object', 'page'),
(3119, 605, '_menu_item_target', ''),
(3120, 605, '_menu_item_classes', 'a:1:{i:0;s:0:\"\";}'),
